def esAnagrama(cadena1, cadena2): 

    # Asegúrate de que ambas cadenas tengan la misma longitud 
    if len(cadena1) != len(cadena2): 
        return False

    # Diccionario para almacenar la frecuencia de caracteres en cadena1 
    count = [0] * 26

    # Para cada carácter en cadena1, incrementa su frecuencia 
    for i in range(len(cadena1)): 
        count[ord(cadena1[i]) - ord('a')] += 1

    # Para cada carácter en cadena2, decrementa su frecuencia 
    for i in range(len(cadena2)): 
        count[ord(cadena2[i]) - ord('a')] -= 1

    # Si alguna frecuencia alcanza un valor negativo, 
    # entonces las cadenas no pueden ser anagramas 
    for i in range(len(count)): 
        if count[i] < 0: 
            return False

    # Si todas las frecuencias son cero, cadena1 y cadena2 
    # son anagramas entre sí 
    return True

